const G=9.80665;function ms2tog(ms2){return ms2/G}
function gtoms2(g){return g*G}
class GBall{constructor(DOMElement,cursorSize=4,maxGValue=8,steps=4,dashed=!0){this.cursorSize=cursorSize;this.maxGValue=maxGValue;this.ball=DOMElement;this.cursor=document.createElement('div');this.cursor.classList.add('csstelemetry-gball-cursor');this.cursor.style.height=this.cursorSize+'%';this.cursor.style.width=this.cursorSize+'%';this.cursor.style.margin=-(this.cursorSize/2)+'% 0 0 '+ -(this.cursorSize/2)+'%';this.ball.appendChild(this.cursor);for(let i=steps-1;i>0;i--){let currentStep=((100/steps)*i);let grid=document.createElement('div');grid.classList.add('csstelemetry-gball');grid.classList.add('csstelemetry-gball-rect');grid.style.width=grid.style.height=currentStep+'%';grid.style.margin=-(currentStep/2)+'% 0 0 '+ -(currentStep/2)+'%';let span=document.createElement('span');span.innerText=((currentStep/100)*this.maxGValue).toFixed(2)+'G';grid.appendChild(span);if(dashed&&(i%2!=0)){grid.style.borderStyle='dashed'}
this.ball.appendChild(grid)}
let span=document.createElement('span');span.innerText=this.maxGValue.toFixed(2)+'G';this.ball.appendChild(span)}
showForce(xAxis,yAxis){let angle=Math.atan(yAxis/xAxis);let maxY=Math.sin(angle)*this.maxGValue;let maxX=Math.cos(angle)*this.maxGValue;let res=[(Math.abs(xAxis)>Math.abs(maxX))?maxX:xAxis,(Math.abs(yAxis)>Math.abs(maxY))?maxY:yAxis];this.cursor.style.left=50+((50-(this.cursorSize/2))*res[0])/this.maxGValue+'%';this.cursor.style.top=50+((50-(this.cursorSize/2))*res[1])/this.maxGValue+'%'}}